import React, { useState } from "react";
import axios from "../utils/axiosInstance";

const PrivacyPolicyForm = () => {
  const [form, setForm] = useState({
    virksomhed_navn: "",
    email: "",
    lokation: "",
    branche: "",
    brugertyper: "",
    kontaktformular: false,
    nyhedsbrev: false,
    webshop: false,
    cookies: false,
  });

  const [generatedHtml, setGeneratedHtml] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/policy/generate", form);
      setGeneratedHtml(res.data.html);
    } catch (err) {
      alert("Fejl under generering af politik");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-4">
      <div className="card">
        <h2 className="text-xl font-semibold mb-4">Generér privatlivspolitik</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input name="virksomhed_navn" placeholder="Virksomhedsnavn" value={form.virksomhed_navn} onChange={handleChange} className="input" required />
          <input name="email" type="email" placeholder="Kontakt-e-mail" value={form.email} onChange={handleChange} className="input" required />
          <input name="lokation" placeholder="Lokation (by, land)" value={form.lokation} onChange={handleChange} className="input" />
          <input name="branche" placeholder="Branche" value={form.branche} onChange={handleChange} className="input" />
          <input name="brugertyper" placeholder="Brugertyper (fx privatkunder)" value={form.brugertyper} onChange={handleChange} className="input" />

          <div className="grid grid-cols-2 gap-4">
            <label className="flex items-center gap-2">
              <input type="checkbox" name="kontaktformular" checked={form.kontaktformular} onChange={handleChange} /> Kontaktformular
            </label>
            <label className="flex items-center gap-2">
              <input type="checkbox" name="nyhedsbrev" checked={form.nyhedsbrev} onChange={handleChange} /> Nyhedsbrev
            </label>
            <label className="flex items-center gap-2">
              <input type="checkbox" name="webshop" checked={form.webshop} onChange={handleChange} /> Webshop
            </label>
            <label className="flex items-center gap-2">
              <input type="checkbox" name="cookies" checked={form.cookies} onChange={handleChange} /> 3rd-party cookies
            </label>
          </div>

          <button type="submit" disabled={loading} className="btn">
            {loading ? "Genererer..." : "Generér politik"}
          </button>
        </form>
      </div>

      {generatedHtml && (
        <div className="card mt-6">
          <h3 className="text-lg font-medium mb-2">Din privatlivspolitik</h3>
          <div dangerouslySetInnerHTML={{ __html: generatedHtml }} />
        </div>
      )}
    </div>
  );
};

export default PrivacyPolicyForm;
