import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import Layout from "../components/Layout";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [url, setUrl] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMe = async () => {
      try {
        const res = await axios.get("/auth/me");
        setUser(res.data);
      } catch (err) {
        navigate("/login");
      }
    };
    fetchMe();
  }, [navigate]);

  const handleAnalyze = async () => {
    if (!url) return;
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/analyze", { url });
      setAnalysis(res.data);
    } catch (err) {
      alert("Analyse fejlede");
    } finally {
      setLoading(false);
    }
  };

  if (!user) return <p>Indl√¶ser dashboard...</p>;

  return (
    <Layout>
      <h2>Hej, {user.company}</h2>
      <p>Du er logget ind som: {user.email}</p>

      <div className="card">
        <h3>GDPR-analyse</h3>
        <input
          type="text"
          placeholder="Indtast din hjemmeside-URL"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <button onClick={handleAnalyze} disabled={loading} style={{ marginLeft: "1rem" }}>
          {loading ? "Analyserer..." : "Analyser"}
        </button>

        {analysis && (
          <div style={{ marginTop: "1.5rem" }}>
            <h4>Compliance Score: {analysis.score}%</h4>
            <p><strong>Mangler:</strong> {analysis.missing?.join(", ") || "Ingen"}</p>
            <p><strong>Forslag:</strong></p>
            <ul>
              {analysis.suggestions?.map((s, i) => <li key={i}>{s}</li>)}
            </ul>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default Dashboard;
