import React, { useState, useEffect, useRef } from "react";
import axios from "../utils/axiosInstance";
import Layout from "../components/Layout";
import { Link } from "react-router-dom";
import {
  AlertTriangle,
  CheckCircle,
  Info,
  FileText,
  Text,
  Globe,
  Sparkles
} from "lucide-react";
import "../styles/global.css";

const Analyser = () => {
  const [mode, setMode] = useState("url");
  const [url, setUrl] = useState("");
  const [policyText, setPolicyText] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [textAnalysis, setTextAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const resultRef = useRef(null);

  const handleURLAnalyze = async () => {
    if (!url) return alert("Indtast en URL");
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/analyze", { url });
      setAnalysis(res.data);
      setTimeout(() => {
        resultRef.current?.scrollIntoView({ behavior: "smooth" });
      }, 100);
    } catch (err) {
      alert("Analyse fejlede");
    } finally {
      setLoading(false);
    }
  };

  const handleTextAnalyze = async () => {
    if (!policyText.trim()) return;
    setLoading(true);
    try {
      const res = await axios.post("/enhancer/analyze-text", { text: policyText });
      setTextAnalysis(res.data);
      setTimeout(() => {
        resultRef.current?.scrollIntoView({ behavior: "smooth" });
      }, 100);
    } catch (err) {
      alert("Tekstanalyse fejlede");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout>
      <div className="card" style={{ maxWidth: "700px", margin: "0 auto" }}>
        <h2 style={{ marginBottom: "1rem" }}>üîç V√¶lg analysetype</h2>
        <div style={{ display: "flex", gap: "1rem", justifyContent: "center", marginBottom: "1rem" }}>
          <button onClick={() => setMode("url")} className={mode === "url" ? "tab-active" : "tab"}>
            <Globe size={16} /> Hjemmeside
          </button>
          <button onClick={() => setMode("text")} className={mode === "text" ? "tab-active" : "tab"}>
            <Text size={16} /> Politiktekst
          </button>
        </div>

        {mode === "url" && (
          <div>
            <input
              type="text"
              placeholder="https://www.eksempel.dk"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              style={{ width: "100%", padding: "10px", borderRadius: "6px", border: "1px solid #ccc", marginBottom: "1rem" }}
            />
            <button onClick={handleURLAnalyze} disabled={loading}>
              {loading ? "Analyserer..." : "üîé Start URL-analyse"}
            </button>
          </div>
        )}

        {mode === "text" && (
          <div>
            <textarea
              rows={10}
              className="form-field"
              placeholder="Inds√¶t din nuv√¶rende politiktekst her..."
              value={policyText}
              onChange={(e) => setPolicyText(e.target.value)}
              style={{ width: "100%", padding: "10px", borderRadius: "6px", border: "1px solid #ccc" }}
            />
            <button onClick={handleTextAnalyze} disabled={loading} style={{ marginTop: "1rem" }}>
              {loading ? "Analyserer..." : "üìÑ Analyser tekst"}
            </button>
          </div>
        )}
      </div>

      {/* URL Analysis Result */}
      {analysis && mode === "url" && (
        <div ref={resultRef} className="card" style={{ marginTop: "2rem", maxWidth: "700px", margin: "2rem auto" }}>
          <h3>üåê Resultat af URL-analyse</h3>
          <p>
            <strong>Compliance Score:</strong>{" "}
            <span style={{
              backgroundColor: analysis.score >= 90 ? "#22C55E" : analysis.score >= 70 ? "#FACC15" : "#EF4444",
              color: "#fff",
              padding: "4px 12px",
              borderRadius: "9999px",
              fontWeight: "bold",
              marginLeft: "0.5rem"
            }}>{analysis.score}%</span>
          </p>
          <p style={{ marginTop: "0.5rem" }}>
            {analysis.missing?.length > 0 ? "üî∏ Mangler: " + analysis.missing.join(", ") : "‚úîÔ∏è Ingen st√∏rre problemer fundet"}
          </p>

          {analysis.suggestions?.length > 0 && (
            <>
              <p style={{ marginTop: "1rem" }}><strong>Forslag til forbedring:</strong></p>
              <ul style={{ paddingLeft: "1.2rem" }}>
                {analysis.suggestions.map((s, i) => (
                  <li key={i}>{s}</li>
                ))}
              </ul>
              <Link
                to="/ai-generator"
                style={{ marginTop: "1rem", display: "inline-block", padding: "10px 16px", backgroundColor: "#2563EB", color: "white", borderRadius: "6px" }}
              >
                Forbedr politik med AI <Sparkles size={16} style={{ marginLeft: 6, verticalAlign: "middle" }} />
              </Link>
            </>
          )}
        </div>
      )}

      {/* Policy Text Result */}
      {textAnalysis && mode === "text" && (
        <div ref={resultRef} className="card" style={{ marginTop: "2rem", maxWidth: "700px", margin: "2rem auto" }}>
          <h3>üìÑ Analyse af politiktekst</h3>
          <ul style={{ paddingLeft: "1.2rem" }}>
            {textAnalysis.recommendations.map((rec, i) => (
              <li key={i} style={{ marginBottom: "0.5rem" }}>‚úîÔ∏è {rec}</li>
            ))}
          </ul>
          <Link
            to="/ai-generator"
            style={{ marginTop: "1rem", display: "inline-block", padding: "10px 16px", backgroundColor: "#2563EB", color: "white", borderRadius: "6px" }}
          >
            Forbedr min politik med AI <Sparkles size={16} style={{ marginLeft: 6, verticalAlign: "middle" }} />
          </Link>
        </div>
      )}
    </Layout>
  );
};

export default Analyser;
