import React, { useState } from "react";
import axios from "../utils/axiosInstance";

const PrivacyPolicyForm = () => {
  const [form, setForm] = useState({
    virksomhed_navn: "",
    email: "",
    lokation: "",
    branche: "",
    brugertyper: "",
    kontaktformular: false,
    nyhedsbrev: false,
    webshop: false,
    cookies: false,
  });

  const [generatedHtml, setGeneratedHtml] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/policy/generate", form);
      setGeneratedHtml(res.data.html);
    } catch (err) {
      alert("Fejl under generering af politik");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <div className="card p-6 rounded-xl bg-white shadow-lg">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6">Generér privatlivspolitik</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {[
            { name: "virksomhed_navn", placeholder: "Virksomhedsnavn", required: true },
            { name: "email", type: "email", placeholder: "Kontakt-e-mail", required: true },
            { name: "lokation", placeholder: "Lokation (by, land)" },
            { name: "branche", placeholder: "Branche" },
            { name: "brugertyper", placeholder: "Brugertyper (fx privatkunder)" },
          ].map(({ name, type = "text", placeholder, required }) => (
            <input
              key={name}
              name={name}
              type={type}
              required={required}
              value={form[name]}
              placeholder={placeholder}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-400"
            />
          ))}

          <div className="grid grid-cols-2 md:grid-cols-2 gap-4 mt-4">
            {[
              { name: "kontaktformular", label: "Kontaktformular" },
              { name: "nyhedsbrev", label: "Nyhedsbrev" },
              { name: "webshop", label: "Webshop" },
              { name: "cookies", label: "3rd-party cookies" },
            ].map(({ name, label }) => (
              <label key={name} className="flex items-center space-x-2 text-gray-700">
                <input
                  type="checkbox"
                  name={name}
                  checked={form[name]}
                  onChange={handleChange}
                  className="h-4 w-4 text-blue-600 border-gray-300 rounded"
                />
                <span>{label}</span>
              </label>
            ))}
          </div>

          <button
            type="submit"
            disabled={loading}
            className="bg-[#7e6e5c] hover:bg-[#5c4e42] text-white py-2 px-6 rounded-md transition"
          >
            {loading ? "Genererer..." : "Generér politik"}
          </button>
        </form>
      </div>

      {generatedHtml && (
        <div className="card mt-10 p-6 rounded-xl bg-white shadow-lg">
          <h3 className="text-xl font-medium mb-4 text-gray-800">Din privatlivspolitik</h3>
          <div
            className="prose max-w-none"
            dangerouslySetInnerHTML={{ __html: generatedHtml }}
          />
        </div>
      )}
    </div>
  );
};

export default PrivacyPolicyForm;
