import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import Layout from "../components/Layout";
import { useNavigate, Link } from "react-router-dom";
import AnalysisResult from "../components/AnalysisResult";
import {
  SearchCheck,
  FileText,
  Zap,
  ShieldCheck,
  AlertCircle
} from "lucide-react";
import "../styles/global.css";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [url, setUrl] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [averageScore, setAverageScore] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMe = async () => {
      try {
        const res = await axios.get("/auth/me");
        setUser(res.data);
      } catch (err) {
        navigate("/login");
      }
    };
    fetchMe();
  }, [navigate]);

  useEffect(() => {
    const fetchAverageScore = async () => {
      try {
        const res = await axios.get("/me/average-score");
        setAverageScore(res.data.average_score);
      } catch (err) {
        console.error("Kunne ikke hente compliance score");
      }
    };
    fetchAverageScore();
  }, []);

  const getScoreClass = (score) => {
    if (score >= 90) return "score-good";
    if (score >= 70) return "score-medium";
    return "score-bad";
  };

  const handleAnalyze = async () => {
    if (!url) return alert("Indtast en URL");
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/analyze", { url });
      setAnalysis(res.data);
    } catch (err) {
      alert("Analyse fejlede");
    } finally {
      setLoading(false);
    }
  };

  if (!user) return <p>Indl√¶ser dashboard...</p>;

  return (
    <Layout>
      {/* Compliance score + CTA */}
      {averageScore !== null && (
        <div class="card" style="margin-bottom: 2rem; text-align: center;">
          <div style="display: flex; flex-direction: column; align-items: center; gap: 1rem;">
            <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="#2563EB" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-shield-check" aria-hidden="true">
              <path d="M20 13c0 5-3.5 7.5-7.66 8.95a1 1 0 0 1-.67-.01C7.5 20.5 4 18 4 13V6a1 1 0 0 1 1-1c2 0 4.5-1.2 6.24-2.72a1.17 1.17 0 0 1 1.52 0C14.51 3.81 17 5 19 5a1 1 0 0 1 1 1z"></path>
              <path d="m9 12 2 2 4-4"></path>
            </svg>
            <div>
              <div style="font-size: 1.1rem; font-weight: 600;">Din gennemsnitlige compliance score</div>
              <div style="margin-top: 0.5rem;">
                <span class="score-badge score-medium">80.7%</span>
              </div>
            </div>
          </div>
          <div style="text-align: center; margin-top: 1rem;">
            <a class="cta-link" href="/analyser" style="background-color: #2563EB; color: #fff; padding: 10px 16px; border-radius: 8px; font-weight: 500; display: inline-block; text-decoration: none;">
              Forbedr din score ‚Üí
            </a>
          </div>
        </div>

      )}

      {/* Hero / intro */}
      <div className="card" style={{ textAlign: "center", padding: "2.5rem" }}>
        <h1 style={{ fontSize: "2rem", marginBottom: "1rem" }}>
          G√∏r din hjemmeside GDPR-kompatibel
        </h1>
        <p style={{ maxWidth: "700px", margin: "0 auto", fontSize: "1.1rem", color: "#555" }}>
          Nexpertia hj√¶lper dig med at analysere din hjemmeside og identificere overtr√¶delser af GDPR ‚Äì hurtigt og nemt.
        </p>
      </div>

      {/* Fordele */}
      <div className="card" style={{ marginTop: "2rem", backgroundColor: "#f9fafb" }}>
        <h2 style={{ textAlign: "center", fontSize: "1.5rem", marginBottom: "1rem" }}>
          Hvorfor bruge Nexpertia?
        </h2>
        <div style={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-around",
          gap: "2rem"
        }}>
          {[
            {
              icon: <SearchCheck size={32} strokeWidth={1.8} />,
              title: "Automatisk analyse",
              desc: "Opdag GDPR-risici uden manuelle tjek."
            },
            {
              icon: <FileText size={32} strokeWidth={1.8} />,
              title: "Klar rapport",
              desc: "Tydelige anbefalinger og konkrete skridt."
            },
            {
              icon: <Zap size={32} strokeWidth={1.8} />,
              title: "Hurtig ops√¶tning",
              desc: "URL + klik = rapport p√• √©t minut."
            },
            {
              icon: <ShieldCheck size={32} strokeWidth={1.8} />,
              title: "Datasikkerhed",
              desc: "Fortrolig og sikker behandling af dine data."
            }
          ].map(({ icon, title, desc }) => (
            <div key={title} style={{ maxWidth: "220px", textAlign: "center" }}>
              <div style={{ marginBottom: "0.5rem", color: "#4F46E5" }}>{icon}</div>
              <h4>{title}</h4>
              <p style={{ fontSize: "0.95rem", color: "#555" }}>{desc}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Analyseformular */}
      <div className="card" style={{ marginTop: "2rem", maxWidth: "600px", marginInline: "auto" }}>
        <h3 style={{ marginBottom: "1rem" }}>üîç Analyser din hjemmeside</h3>
        <input
          type="text"
          placeholder="https://www.eksempel.dk"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          style={{
            width: "100%",
            padding: "12px",
            fontSize: "1rem",
            borderRadius: "6px",
            border: "1px solid #ccc",
            marginBottom: "1rem"
          }}
        />
        <button
          onClick={handleAnalyze}
          disabled={loading}
          style={{
            width: "100%",
            height: "44px",
            fontSize: "1rem",
            fontWeight: "bold",
            borderRadius: "6px",
            backgroundColor: "#1E3A8A",
            color: "#fff",
            border: "none",
            cursor: "pointer"
          }}
        >
          {loading ? "Analyserer..." : "üîé Analyser"}
        </button>
      </div>

      {/* Resultatvisning */}
      {analysis && (
        <div className="analysis-layout">
          <AnalysisResult analysis={analysis} withTitle />
        </div>
      )}

      {/* Hvis analyse er tilg√¶ngelig og der er mangler */}
      {analysis?.missing?.length > 0 && (
        <div className="card" style={{ marginTop: "2rem" }}>
          <h3 style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
            <AlertCircle size={20} /> Manglende punkter i din analyse
          </h3>
          <ul style={{ marginTop: "0.5rem", paddingLeft: "1rem", color: "#B91C1C" }}>
            {analysis.missing.map((m, i) => (
              <li key={i}>{m}</li>
            ))}
          </ul>
        </div>
      )}

      <div style={{ textAlign: "center", marginTop: "2rem" }}>
        <Link
          to="/politik-historik"
          style={{
            textDecoration: "none",
            padding: "12px 20px",
            borderRadius: "8px",
            backgroundColor: "#2563EB",
            color: "#fff",
            fontWeight: 500
          }}
        >
          Se dine tidligere politikker
        </Link>
      </div>
    </Layout>
  );
};

export default Dashboard;
