import React from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import {
  ShieldCheck,
  FileText,
  History,
  User,
  LogOut,
  Globe,
  LayoutDashboard
} from "lucide-react";
import logo from "../assets/logo-light.png";
import "../styles/global.css";

const Layout = ({ children }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const isActive = (path) => location.pathname === path;

  return (
    <div>
      {/* Header navigation */}
      <header className="header-bar">
        <div className="nav-left">
          <Link to="/dashboard">
            <img src={logo} alt="GDPR logo" className="nav-logo" />
          </Link>
          <h1 className="nav-title">Nexpertia.dk</h1>
        </div>

        <nav className="nav-right">
          <div className="dropdown">
            <button className="dropbtn">Menu â–¾</button>
            <div className="dropdown-content">
              <Link to="/dashboard" className={isActive("/dashboard") ? "active" : ""}>
                <LayoutDashboard size={16} style={{ marginRight: 6 }} />
                Dashboard
              </Link>
              <Link to="/analyser" className={isActive("/analyser") ? "active" : ""}>
                <ShieldCheck size={16} style={{ marginRight: 6 }} />
                GDPR-analyse
              </Link>
              <Link to="/privatlivspolitik" className={isActive("/privatlivspolitik") ? "active" : ""}>
                <FileText size={16} style={{ marginRight: 6 }} />
                Privatlivspolitik
              </Link>
              <Link to="/politik-historik" className={isActive("/politik-historik") ? "active" : ""}>
                <History size={16} style={{ marginRight: 6 }} />
                Historik
              </Link>
              <Link to="/profile" className={isActive("/profile") ? "active" : ""}>
                <User size={16} style={{ marginRight: 6 }} />
                Min profil
              </Link>
            </div>
          </div>

          <button className="logout-btn" onClick={handleLogout}>
            <LogOut size={16} style={{ marginRight: 6, verticalAlign: "middle" }} />
            Log ud
          </button>
        </nav>
      </header>

      <main className="dashboard">
        <div className="container">
          {children}
        </div>
      </main>
    </div>
  );
};

export default Layout;
