// pages/BlogPost.jsx
import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import { useParams, Link } from "react-router-dom";

const BlogPost = () => {
  const { slug } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    axios.get(`/api/blog/${slug}`).then((res) => setPost(res.data)).catch(() => setPost(null));
  }, [slug]);

  if (!post) return <div className="p-6">Indlægget blev ikke fundet.</div>;

  return (
    <div className="max-w-3xl mx-auto p-6 prose prose-neutral">
      <Link to="/blog" className="text-blue-600 text-sm">&larr; Tilbage til blog</Link>
      <h1>{post.title}</h1>
      <p className="text-sm text-gray-500 mb-4">Skrevet af {post.author} – {new Date(post.created_at).toLocaleDateString("da-DK")}</p>
      <div dangerouslySetInnerHTML={{ __html: post.content }} />
    </div>
  );
};

export default BlogPost;
