import React, { useState } from "react";
import axios from "../utils/axiosInstance";
import Layout from "../components/Layout"; // ← tilføjet
import "../styles/privacyform.css";
import {
  Download,
  ShieldCheck
} from "lucide-react";

const AIGenerator = () => {
  const [form, setForm] = useState({
    virksomhed_navn: "",
    email: "",
    lokation: "",
    branche: "",
    brugertyper: "",
    kontaktformular: false,
    nyhedsbrev: false,
    webshop: false,
    cookies: false,
    trackingværktøjer: "",
    databehandlere: "",
    opbevaringsperiode: "",
    målgruppe: "",
    særlige_hensyn: ""
  });

  const [generatedHtml, setGeneratedHtml] = useState(null);
  const [generatedId, setGeneratedId] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/policy/generate", form);
      setGeneratedHtml(res.data.html);
      setGeneratedId(res.data.id);
    } catch (err) {
      alert("Fejl under generering af politik");
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`/gdpr/policy/${generatedId}/pdf`, {
        responseType: "blob",
        headers: {
          Authorization: `Bearer ${token}`,
        },
        withCredentials: true,
      });

      const blob = new Blob([response.data], { type: "application/pdf" });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.download = `privatlivspolitik_${generatedId}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (err) {
      alert("Kunne ikke hente PDF – måske din login-session er udløbet.");
    }
  };

  return (
    <Layout> {/* ← HER er Layout tilføjet */}
      <div className="policy-form-container">
        <div className="policy-form-card">
          <h2 style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
            <ShieldCheck size={20} /> Generér GDPR-dokument
          </h2>

          <form onSubmit={handleSubmit} className="policy-form">
            {/* Tekstfelter */}
            {[
              { name: "virksomhed_navn", placeholder: "Virksomhedsnavn", required: true },
              { name: "email", type: "email", placeholder: "Kontakt-e-mail", required: true },
              { name: "lokation", placeholder: "Lokation (by, land)" },
              { name: "branche", placeholder: "Branche" },
              { name: "brugertyper", placeholder: "Brugertyper (fx privatkunder, erhverv)" },
              { name: "målgruppe", placeholder: "Primær målgruppe (fx danske brugere, EU)" },
            ].map((field, idx) => (
              <input
                key={idx}
                name={field.name}
                type={field.type || "text"}
                placeholder={field.placeholder}
                value={form[field.name]}
                onChange={handleChange}
                required={field.required || false}
              />
            ))}

            {/* Tekstområder */}
            {[
              { name: "trackingværktøjer", placeholder: "Hvilke trackingværktøjer bruges? (fx Google Analytics, Meta Pixel)" },
              { name: "databehandlere", placeholder: "Nævn eksterne databehandlere (fx Mailchimp, Stripe)" },
              { name: "opbevaringsperiode", placeholder: "Hvor længe opbevares data typisk? (fx 12 mdr)" },
              { name: "særlige_hensyn", placeholder: "Evt. særlige forhold (fx følsomme data, børn)" }
            ].map((field, idx) => (
              <textarea
                key={idx}
                name={field.name}
                placeholder={field.placeholder}
                value={form[field.name]}
                onChange={handleChange}
                className="policy-textarea" // brug samme stil
                style={{
                  width: "100%",
                  padding: "10px",
                  borderRadius: "6px",
                  border: "1px solid #ccc",
                  fontSize: "1rem",
                  marginBottom: "1rem"
                }}
              />
            ))}

            {/* Checkbox-gruppe */}
            <div className="policy-checkboxes">
              {[
                { name: "kontaktformular", label: "Kontaktformular" },
                { name: "nyhedsbrev", label: "Nyhedsbrev" },
                { name: "webshop", label: "Webshop" },
                { name: "cookies", label: "3rd-party cookies" }
              ].map((cb, idx) => (
                <label key={idx}>
                  <input
                    type="checkbox"
                    name={cb.name}
                    checked={form[cb.name]}
                    onChange={handleChange}
                  />
                  {cb.label}
                </label>
              ))}
            </div>

            <button type="submit" disabled={loading}>
              {loading ? "Genererer..." : "Generér politik"}
            </button>
          </form>
        </div>

        {generatedHtml && (
          <div className="policy-form-card" style={{ marginTop: "2rem" }}>
            <h3>Din politik</h3>
            <div dangerouslySetInnerHTML={{ __html: generatedHtml }} />
            {generatedId && (
              <button
                onClick={handleDownload}
                className="download-btn"
                style={{ marginTop: "1rem" }}
              >
                <Download size={16} style={{ marginRight: 6 }} />
                Download som PDF
              </button>
            )}
          </div>
        )}
      </div>
    </Layout>
  );
};

export default AIGenerator;
