import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import Layout from "../components/Layout";
import { useNavigate } from "react-router-dom";
import HistoryList from "../components/HistoryList";
import AnalysisResult from "../components/AnalysisResult";



const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [url, setUrl] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMe = async () => {
      try {
        const res = await axios.get("/auth/me");
        setUser(res.data);
      } catch (err) {
        navigate("/login");
      }
    };
    fetchMe();
  }, [navigate]);

  const handleAnalyze = async () => {
    if (!url) return alert("Indtast en URL");
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/analyze", { url });
      setAnalysis(res.data);
    } catch (err) {
      alert("Analyse fejlede");
    } finally {
      setLoading(false);
    }
  };

  if (!user) return <p>Indl√¶ser dashboard...</p>;

  return (
    <Layout>
      <div className="card">
        <h2>Hej, {user.company}</h2>
        <p>Du er logget ind som: {user.email}</p>

        <div style={{ marginTop: "2rem" }}>
          <h3>GDPR-analyse</h3>
          <input
            type="text"
            placeholder="Indtast din hjemmeside-URL"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            style={{
              padding: "12px",
              width: "70%",
              maxWidth: "400px",
              borderRadius: "6px",
              border: "1px solid #ccc",
              marginBottom: "1rem"
            }}
          />
          <button
            onClick={handleAnalyze}
            disabled={loading}
            style={{
              padding: "12px 24px",
              backgroundColor: "#3e3e3e",
              color: "white",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer"
            }}
          >
            {loading ? "Analyserer..." : "Analyser"}
          </button>
        </div>

        <AnalysisResult analysis={analysis} />

        
      </div>
      <HistoryList />
    </Layout>
  );
};

export default Dashboard;
