import React, { useState, useEffect } from "react";
import axios from "../utils/axiosInstance";
import Layout from "../components/Layout";
import "../styles/global.css";
import { Link } from "react-router-dom";
import {
  AlertTriangle,
  CheckCircle,
  Info,
  ShieldCheck,
  FileText,
  BadgeAlert,
  ThumbsUp
} from "lucide-react";

const Analyser = () => {
  const [url, setUrl] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem("latestAnalysis");
    if (saved) {
      const parsed = JSON.parse(saved);
      setUrl(parsed.url || "");
      setAnalysis(parsed.analysis || null);
    }
  }, []);

  const handleAnalyze = async () => {
    if (!url) return alert("Indtast en URL");
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/analyze", { url });
      const data = res.data;
      setAnalysis(data);
      localStorage.setItem("latestAnalysis", JSON.stringify({ url, analysis: data }));
    } catch (err) {
      alert("Analyse fejlede");
    } finally {
      setLoading(false);
    }
  };

  const getScoreColor = (score) => {
    if (score >= 90) return "#22C55E"; // grøn
    if (score >= 70) return "#FACC15"; // gul
    return "#EF4444"; // rød
  };

  return (
    <Layout>
      <div className="card" style={{ maxWidth: "700px", margin: "0 auto" }}>
        <h2 style={{ marginBottom: "1rem" }}>🔍 Udfør GDPR-analyse</h2>
        <p style={{ marginBottom: "1rem", color: "#555" }}>
          Indtast din virksomheds eller hjemmesides URL for at tjekke, om den overholder GDPR-krav.
        </p>
        <input
          type="text"
          placeholder="https://www.eksempel.dk"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          style={{
            width: "100%",
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
            marginBottom: "1rem",
            fontSize: "1rem"
          }}
        />
        <button onClick={handleAnalyze} disabled={loading}>
          {loading ? "Analyserer..." : "🔎 Start analyse"}
        </button>
      </div>

      {analysis && (
        <div className="card" style={{ marginTop: "2rem", maxWidth: "700px", margin: "2rem auto" }}>
          <h3>📋 Resultat af seneste analyse</h3>
          <p>
            <strong>Compliance Score:</strong>{" "}
            <span style={{
              backgroundColor: getScoreColor(analysis.score),
              color: "#fff",
              padding: "4px 12px",
              borderRadius: "9999px",
              fontWeight: "bold",
              marginLeft: "0.5rem"
            }}>
              {analysis.score}%
            </span>
          </p>

          <div style={{ marginTop: "1rem" }}>
            <strong>Mangler:</strong>
            {analysis.missing?.length > 0 ? (
              analysis.missing.map((m, i) => (
                <div key={i} style={{ display: "flex", alignItems: "center", marginBottom: "0.4rem", color: "#000" }}>
                  <AlertTriangle color="#DC2626" size={18} style={{ marginRight: "8px" }} />
                  <span>{m}</span>
                </div>
              ))
            ) : (
              <div style={{ display: "flex", alignItems: "center", color: "#22C55E", marginTop: "0.5rem" }}>
                <CheckCircle size={18} style={{ marginRight: "8px" }} />
                Ingen større problemer fundet
              </div>
            )}
          </div>

          {analysis.suggestions?.length > 0 && (
            <>
              <p style={{ marginTop: "1rem" }}><strong>Forslag til forbedring:</strong></p>
              <ul style={{ paddingLeft: "1.2rem" }}>
                {analysis.suggestions.map((s, i) => {
                  const isCritical = s.level === "critical";
                  return (
                    <li key={i} style={{ display: "flex", alignItems: "center", marginBottom: "0.5rem", color: "#000" }}>
                      {isCritical ? (
                        <AlertTriangle color="#DC2626" size={18} style={{ marginRight: "8px" }} />
                      ) : (
                        <Info color="#EAB308" size={18} style={{ marginRight: "8px" }} />
                      )}
                      <span>{s.text}</span>
                      <span style={{
                        marginLeft: "auto",
                        padding: "2px 8px",
                        borderRadius: "6px",
                        fontSize: "0.75rem",
                        backgroundColor: isCritical ? "#DC2626" : "#EAB308",
                        color: "#fff"
                      }}>
                        {isCritical ? "Kritisk" : "Nice to have"}
                      </span>
                    </li>
                  );
                })}
              </ul>
            </>
          )}

          {analysis.checkedFiles?.length > 0 && (
            <div style={{ marginTop: "1rem" }}>
              <strong>Analyserede elementer:</strong>
              <ul style={{ paddingLeft: "1.2rem" }}>
                {analysis.checkedFiles.map((file, i) => (
                  <li key={i} style={{ display: "flex", alignItems: "center", marginBottom: "0.3rem", color: "#000" }}>
                    <FileText size={18} color="#2563EB" style={{ marginRight: "8px" }} />
                    {file}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      <div style={{ textAlign: "center", marginTop: "2rem" }}>
        <Link
          to="/politik-historik"
          style={{
            textDecoration: "none",
            padding: "10px 16px",
            borderRadius: "8px",
            backgroundColor: "#2563EB",
            color: "#fff",
            fontWeight: 500,
            display: "inline-block"
          }}
        >
          Gå til tidligere politikker
        </Link>
      </div>
    </Layout>
  );
};

export default Analyser;
