import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import { useNavigate } from "react-router-dom";
import Layout from "../components/Layout";


const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [url, setUrl] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const navigate = useNavigate();

  const logout = async () => {
    await axios.post("http://localhost:5000/api/auth/logout", {}, {
      withCredentials: true
    });
    localStorage.removeItem("token");
    navigate("/");
  };

  useEffect(() => {
    const fetchMe = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/auth/me", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`
          }
        });
        setUser(res.data);
      } catch (err) {
        navigate("/");
      }
    };
    fetchMe();
  }, [navigate]);

  const handleAnalyze = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/gdpr/analyze", { url });
      setAnalysis(res.data);
    } catch (err) {
      alert("Analyse fejlede");
    }
  };

  if (!user) return <p>Indl√¶ser dashboard...</p>;

  return (
    <Layout>
      <h2>Hej, {user.company}</h2>
      <p>Du er logget ind som: {user.email}</p>
      {/* resten af dashboardet her */}
      <div style={{ marginTop: "2rem" }}>
        <h3>GDPR-analyse</h3>
        <input
          type="text"
          placeholder="Indtast din hjemmeside-URL"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          style={{ padding: "10px", width: "300px" }}
        />
        <button onClick={handleAnalyze} style={{ marginLeft: "10px", padding: "10px" }}>
          Analyser
        </button>
      </div>

      {analysis && (
        <div style={{ marginTop: "2rem", background: "#eee", padding: "1rem" }}>
          <h4>Compliance Score: {analysis.score}%</h4>
          <p><strong>Mangler:</strong> {analysis.missing?.join(", ")}</p>
          <p><strong>Forslag:</strong></p>
          <ul>
            {analysis.suggestions?.map((s, i) => (
              <li key={i}>{s}</li>
            ))}
          </ul>
        </div>
      )}

      <button onClick={logout} style={{ marginTop: "3rem", padding: "10px" }}>
        Log ud
      </button>
    </Layout>
  );
};

export default Dashboard;
