// pages/BlogList.jsx
import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import { Link } from "react-router-dom";
import { Card, CardContent } from "@/components/ui/card";

const BlogList = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios.get("/api/blog").then((res) => setPosts(res.data));
  }, []);

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-6">ðŸ“š Seneste blogindlÃ¦g</h1>
      <div className="space-y-4">
        {posts.map((post) => (
          <Card key={post.id}>
            <CardContent className="p-4">
              <Link to={`/blog/${post.slug}`} className="text-lg font-semibold text-blue-600 hover:underline">
                {post.title}
              </Link>
              <div className="text-sm text-gray-500">{new Date(post.created_at).toLocaleDateString("da-DK")}</div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default BlogList;
