import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import Layout from "../components/Layout";
import { useNavigate } from "react-router-dom";
import HistoryList from "../components/HistoryList";
import AnalysisResult from "../components/AnalysisResult";
import "../styles/global.css"; // Ny CSS-fil til moderne stil

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [url, setUrl] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMe = async () => {
      try {
        const res = await axios.get("/auth/me");
        setUser(res.data);
      } catch (err) {
        navigate("/login");
      }
    };
    fetchMe();
  }, [navigate]);

  const handleAnalyze = async () => {
    if (!url) return alert("Indtast en URL");
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/analyze", { url });
      setAnalysis(res.data);
    } catch (err) {
      alert("Analyse fejlede");
    } finally {
      setLoading(false);
    }
  };

  if (!user) return <p>Indl√¶ser dashboard...</p>;

  return (
    <Layout>
      <div className="dashboard-card">
        <h2>Hej, {user.company}</h2>
        <p>Du er logget ind som: {user.email}</p>

        <div className="analyze-section">
          <h3>üîç Udf√∏r GDPR-analyse</h3>
          <input
            type="text"
            placeholder="Indtast din hjemmeside-URL"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            className="url-input"
          />
          <button onClick={handleAnalyze} disabled={loading} className="analyze-button">
            {loading ? "Analyserer..." : "Analyser"}
          </button>
        </div>
      </div>

      <div className="analysis-layout">
        {analysis && (
          <>
            <h3 className="section-title">üìã Resultat af seneste analyse</h3>
            <AnalysisResult analysis={analysis} />
          </>
        )}
        <div style={{ width: "100%" }}>
          <h3 className="section-title">üïò Tidligere analyser</h3>
          <HistoryList />
        </div>
      </div>
    </Layout>
  );
};

export default Dashboard;
