import React, { useState, useRef } from "react";
import axios from "../utils/axiosInstance";
import Layout from "../components/Layout";
import { Link } from "react-router-dom";
import {
  Globe,
  Text,
  UploadCloud,
  Sparkles
} from "lucide-react";
import "../styles/global.css";

const Analyser = () => {
  const [url, setUrl] = useState("");
  const [policyText, setPolicyText] = useState("");
  const [textFile, setTextFile] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [textAnalysis, setTextAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const resultRef = useRef(null);

  const handleURLAnalyze = async () => {
    if (!url) return alert("Indtast en URL");
    setLoading(true);
    try {
      const res = await axios.post("/gdpr/analyze", { url });
      setAnalysis(res.data);
      setTimeout(() => {
        resultRef.current?.scrollIntoView({ behavior: "smooth" });
      }, 100);
    } catch (err) {
      alert("Analyse fejlede");
    } finally {
      setLoading(false);
    }
  };

  const handleTextAnalyze = async () => {
    if (!policyText.trim()) return;
    setLoading(true);
    try {
      const res = await axios.post("/enhancer/analyze-text", { text: policyText });
      setTextAnalysis(res.data);
      setTimeout(() => {
        resultRef.current?.scrollIntoView({ behavior: "smooth" });
      }, 100);
    } catch (err) {
      alert("Tekstanalyse fejlede");
    } finally {
      setLoading(false);
    }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      setPolicyText(evt.target.result);
    };
    reader.readAsText(file);
    setTextFile(file);
  };

  return (
    <Layout>
      <div className="policy-form-container">
        <div className="policy-form-card">
          <h2 style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
            <Globe size={20} /> Analyse af hjemmeside
          </h2>
          <form onSubmit={(e) => { e.preventDefault(); handleURLAnalyze(); }} className="policy-form">
            <input
              type="text"
              placeholder="https://www.eksempel.dk"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              className="form-field"
            />
            <button type="submit" className="button" disabled={loading}>
              {loading ? "Analyserer..." : "🔎 Start URL-analyse"}
            </button>
          </form>
        </div>

        <div className="policy-form-card" style={{ marginTop: "2rem" }}>
          <h2 style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
            <Text size={20} /> Analyse af politiktekst
          </h2>

          <form onSubmit={(e) => { e.preventDefault(); handleTextAnalyze(); }} className="policy-form">
            <div className="upload-center">
              <label className="button inline-flex items-center gap-2">
                <UploadCloud size={16} /> Upload fil
                <input type="file" accept=".txt,.md" onChange={handleFileUpload} hidden />
              </label>
            </div>

            <textarea
              rows={8}
              placeholder="Indsæt din nuværende politiktekst her..."
              value={policyText}
              onChange={(e) => setPolicyText(e.target.value)}
              className="form-field"
              style={{ resize: "vertical" }}
            />

            <button type="submit" className="button" disabled={loading}>
              {loading ? "Analyserer..." : "📄 Analyser tekst"}
            </button>
          </form>
        </div>

        {(analysis || textAnalysis) && (
          <div ref={resultRef} className="policy-form-card" style={{ marginTop: "2rem" }}>
            {analysis && (
              <>
                <h3>🌐 Resultat af URL-analyse</h3>
                <p>
                  <strong>Compliance Score:</strong>
                  <span
                    className={`score-badge ${
                      analysis.score >= 90
                        ? "score-good"
                        : analysis.score >= 70
                        ? "score-medium"
                        : "score-bad"
                    }`}
                  >
                    {analysis.score}%
                  </span>
                </p>
                <p style={{ marginTop: "0.5rem" }}>
                  {analysis.missing?.length > 0
                    ? "🔸 Mangler: " + analysis.missing.join(", ")
                    : "✔️ Ingen større problemer fundet"}
                </p>
                {analysis.suggestions?.length > 0 && (
                  <>
                    <p style={{ marginTop: "1rem", fontWeight: 600 }}>
                      Forslag til forbedring:
                    </p>
                    <ul className="list-disc pl-6 mt-2">
                      {analysis.suggestions.map((s, i) => (
                        <li key={i}>{s}</li>
                      ))}
                    </ul>
                    <Link
                      to="/ai-generator"
                      className="button mt-4 inline-flex items-center gap-2"
                    >
                      Forbedr politik med AI <Sparkles size={16} />
                    </Link>
                    <Link
                      to="/ai-generator"
                      className="button mt-2 inline-flex items-center gap-2"
                    >
                      Generér privatlivspolitik baseret på denne analyse <Sparkles size={16} />
                    </Link>
                  </>
                )}
              </>
            )}

            {textAnalysis && (
              <>
                <h3>📄 Analyse af politiktekst</h3>
                <ul className="list-disc pl-6">
                  {textAnalysis.recommendations.map((rec, i) => (
                    <li key={i}>{rec}</li>
                  ))}
                </ul>
                <Link
                  to="/ai-generator"
                  className="button mt-4 inline-flex items-center gap-2"
                >
                  Generér privatlivspolitik baseret på denne analyse <Sparkles size={16} />
                </Link>
                <Link
                  to="/ai-generator"
                  className="button mt-4 inline-flex items-center gap-2"
                >
                  Forbedr min politik med AI <Sparkles size={16} />
                </Link>
              </>
            )}
          </div>
        )}
      </div>
    </Layout>
  );
};

export default Analyser;
