import React, { useEffect, useState } from "react";
import axios from "../utils/axiosInstance";
import Layout from "../components/Layout";

const Profile = () => {
  const [form, setForm] = useState({
    company_name: "",
    website_url: "",
    cvr: "",
    contact_email: ""
  });

  useEffect(() => {
    const fetchProfile = async () => {
      const res = await axios.get("/auth/me");
      setForm({
        company_name: res.data.company_name || "",
        website_url: res.data.website_url || "",
        cvr: res.data.cvr || "",
        contact_email: res.data.contact_email || ""
      });
    };
    fetchProfile();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.put("/auth/me", form);
    alert("Profil opdateret");
  };

  return (
    <Layout>
      <div className="card">
        <h2 className="text-xl font-semibold mb-4">Virksomhedsprofil</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input name="company_name" placeholder="Virksomhedsnavn" value={form.company_name} onChange={handleChange} className="input" />
          <input name="website_url" placeholder="Website" value={form.website_url} onChange={handleChange} className="input" />
          <input name="cvr" placeholder="CVR-nummer" value={form.cvr} onChange={handleChange} className="input" />
          <input name="contact_email" placeholder="Kontaktmail" value={form.contact_email} onChange={handleChange} className="input" />
          <button type="submit" className="btn">Gem Ã¦ndringer</button>
        </form>
      </div>
    </Layout>
  );
};

export default Profile;
